const $=(s,c=document)=>c.querySelector(s);const $$=(s,c=document)=>Array.from(c.querySelectorAll(s));
const ROUTES={fibre:"dpp-pages/index.html#fibre",yarn:"dpp-pages/index.html#yarn",fabric:"dpp-pages/index.html#fabric",chemicals:"dpp-pages/chemicals.html",finishing:"dpp-pages/index.html#finishing",garment:"dpp-pages/index.html#garment",energy:"dpp-pages/energy.html",packaging:"dpp-pages/index.html#packaging",logistics:"dpp-pages/index.html#logistics",delivery:"dpp-pages/index.html#delivery",retail:"dpp-pages/index.html#retail",office:"dpp-pages/index.html#office",it:"dpp-pages/index.html#it",suppliesWaste:"dpp-pages/index.html#supplies-waste",employeeTransport:"dpp-pages/index.html#employee-transport"};
function openDPP(k){const u=ROUTES[k];if(!u){console.warn("ROUTE not found:",k);return}window.location.href=u}
function enableKeyboardActivation(el){el.addEventListener("keydown",e=>{if(e.key==="Enter"||e.key===" "){e.preventDefault();el.click()}})}
document.addEventListener("DOMContentLoaded",()=>{$$(".link-card").forEach(c=>{const k=c.getAttribute("data-key");c.addEventListener("click",()=>openDPP(k));enableKeyboardActivation(c)});$$(".qa-btn").forEach(b=>{const k=b.getAttribute("data-key");b.addEventListener("click",()=>openDPP(k))});const el=$("#kpi-updated");if(el)el.textContent=new Date().toLocaleString()});
